/******************** Body ********************/

body
{
    text-align: center;
    padding-top: 150px;
    padding-bottom: 100px;

}

h1
{
    color: #36607E;
}

/* all the paragraphs of text */
#homeWelcome,
.aboutText
{
    width: 80%;
    margin: 0px auto 70px auto;
}

/* paragraph on the home page */
#homeWelcome span
{
    text-decoration: underline;
}

/* paragraphs on the about page */
#aboutMyanmar
{
    margin-bottom: 70px;
}

#aboutStateside
{
    margin-bottom: 50px;
}




/******************** Navbar ********************/

.navbar
{
    box-shadow: 0px 1px 10px #e8e8e8;
}

/* donate button */
#overide .donatebtn
{
    background: #36607E;
    color: #ffffff;
    padding: 4px 15px 4px 15px;
    margin: auto;
    border-radius: 20px;
}

/* contact button */
#overide .contactbtn
{
    background: #ffffff;
    color: #36607E;
    padding: 4px 15px 4px 15px;
    margin: auto;
    border: 2px solid #36607E;
    border-radius: 20px;
}

/* seperation between donate and contact buttons */
.navbar-nav span
{
    width: 20px;
}

/* all links in the navbar */
.navbar a
{
    color: grey;
}
.navbar a.active,
.navbar a:hover
{
    color: #204E70;
}

/* navbar toggler */
.navbar-toggler
{
    border-style: none;
}
.navbar-toggler:hover
{
    border: 1px solid #e8e8e8;
}
.navbar-toggler:focus
{
    outline: 0;
    outline-offset: 0;
    background-image: none;
    -webkit-box-shadow: none;
    box-shadow: none;
}




/******************** HOME PAGE: Kids profiles ********************/

img
{
    width: 100%;
    border-radius: 2px;
    margin-bottom: 5px;
    margin-top: 20px;
    box-shadow: 2px 2px 3px grey;
}

/* figcaption element */
.personalInfo
{
    font-size: .8em;
    padding: 5px;
}

/* nested divs */
.personalInfo div
{
    margin-bottom: 10px;
}

/* nested span */
.personalInfo span
{
    margin-left: 5px;
    margin-right: 5px;
}

/* the story div */
.story
{
    margin-top: 10px;
}

/* colored status */
.sponsored
{
    background: #20bf6b;
    color: white;
    display: inline;
    padding: 5px 9px 5px 9px;
    border-radius: 2px;
}
.unsponsored
{
    background: #ee5253;
    color: white;
    display: inline;
    padding: 5px 9px 5px 9px;
    border-radius: 2px;
}




/******************** ABOUT PAGE: Staff Profiles ********************/

/* the figcaptions on the about page */
.staffInfo
{
    font-size: .8em;
}




/******************** Modals ********************/

#sponsorDiv
{
    margin: 20px 10px 60px 10px;
}

.modal-body
{
    padding-bottom: 15px;
}
